			$cookies.chequeno = &quot;&quot;;
			$cookies.bank = &quot;&quot;;
			$cookies.tot_amt = &quot;&quot;;
			$cookies.adv_amt = &quot;&quot;;			
			return;
		}
				else if(data.success == 2)
				{
